class City extends BaseCity
{
   public static function getById($id) {
		$q  = Doctrine_Query::create()
             ->from('City p')
             ->where('p.id= ?',$id);
        $place = $q->fetchOne();
             
        return $place;
	}

	public function getAll() {
		$q = Doctrine_Query::create()
			->from('City p');
		$rows = $q->fetchArray();

		return $rows;
	}
}